import type { History, HistoryOptions, LocationTypeMap } from './index';
/**
 * Utils
 */
export interface StopListener {
    (): void;
}
export interface UpdateState {
    (state: object, path: string): void;
}
/**
 * createBrowserHistory
 *
 * Creates and returns a history object that uses HTML5's history API
 * (pushState, replaceState, and the popstate event) to manage history.
 * This is the recommended method of managing history in browsers because
 * it provides the cleanest URLs.
 *
 * Note: In browsers that do not support the HTML5 history API full
 * page reloads will be used to preserve clean URLs. You can force this
 * behavior using { forceRefresh: true } in options.
 */
export default function createBrowserHistory(options?: HistoryOptions): History<LocationTypeMap['NORMAL']['Base'], LocationTypeMap['NORMAL']['Intact']>;
